
var Tabs = require('../components/Tabs.jsx');

describe('Tabs', function() {

	var target;

	before(function() {
		$('div#test').remove();
		$('<div id="test"></div>').appendTo(document.body);
	});

	it('create pure Tabs', function(done) {
		target = React.render((
			<Tabs
				tabLabels={['aaa','bbb','ccc']}
				defaultIndex={1}
			></Tabs>
		), $('#test').get(0));
		expect($('a.mdl-tabs__tab').length).equal(3);
		return done();
	});

});
