
var Toggle = require('../components/Toggle.jsx');

describe('Toggle', function() {

	var target;

	before(function() {
		$('div#test').remove();
		$('<div id="test"></div>').appendTo(document.body);
	});

	it('create pure toggle', function(done) {
		target = React.render((
			<Toggle
				type="checkbox"
				text="checkbox"
			></Toggle>
		), $('#test').get(0));
		expect($('input[type=checkbox]').length).gt(0);
		return done();
	});

	it('create pure toggle', function(done) {
		target = React.render((
			<Toggle
				type="radio"
				text="radio"
				name='name'
				value='value'
			></Toggle>
		), $('#test').get(0));
		expect($('input[type=radio]').length).gt(0);
		return done();
	});

});
